mapper.insert:
insert into grocery(
	seq
	,gcName
	,gcCategory
	,gcPrice
	,gcStock
	,gcUseNy
	,gcDelNy
	,gcRegDateTime
)values(
	#{seq}
	,#{gcName}
	,#{gcCategory}
	,#{gcPrice}
	,#{gcStock}
	,#{gcUseNy}
	,0
	,now()
)

	Select last_insert_id()

