mapper.insert:
insert into member(
	seq
	,mbName
	,mbId
	,mbPassword
	,mbEmail
    ,mbRegDateTime
    ,mbDelNy
)values(
	#{seq}
	,#{mbName}
	,#{mbId}
	,#{mbPassword}
	,#{mbEmail}
   	,now()
    ,0
)

	Select last_insert_id()

mapper.update:
update member
set
mbName = #{mbName}
,mbId = #{mbId}
,mbPassword  = #{mbPassword}
,mbEmail  = #{mbEmail}
,mbModDateTime = now()
where 1=1
AND seq = #{seq}
